import { Link } from "react-router-dom";
import Bookshelf from "./bookshelf";
import { useState, useEffect } from "react";
import { get, getAll, update } from '../BooksAPI';

const BookList = () => {
    const [books, setBooks] = useState([])

    useEffect(() => {
        const getBooks = async () => {
            const res = await getAll();
            setBooks(res)
        }
        getBooks();
    }, [])

    const moveBook = async (key, value) => {
        console.log(key, value)
        const res = await update(key, value)
        var arr = [];
        Object.keys(res).map(key => {
            res[key].map(elem => {
                let book = books.filter(book => book.id === elem)[0]
                book.shelf = key
                arr = [...arr, book]
            })
        });
        console.log(arr)
        setBooks(arr);
    }

    return(
    <div className="app">
        <div className="list-books">
        <div className="list-books-title">
            <h1>MyReads</h1>
        </div>
        <div className="list-books-content">
            <ListBooks moveBook={moveBook} books={books}/>
        </div>
        <div className="open-search">
            <Link to="/search">Add a book</Link>
        </div>
        </div>
    </div>
    )
}

const ListBooks = ({ moveBook, books}) => {

    useEffect(() => {
        if(books.length > 0){
            setCurrentlyReading(books.filter(book => book.shelf === "currentlyReading"))
            setRead(books.filter(book => book.shelf === "read"))
            setWantToRead(books.filter(book => book.shelf === "wantToRead"))
        }
    },[books])

    return(
        <div>
            <Bookshelf title="Currently Reading" books={currentlyReading} moveBook={moveBook}/>
            <Bookshelf title="Read" shelf="read" books={read} moveBook={moveBook}/>
            <Bookshelf title="Want to Read" books={wantToRead} moveBook={moveBook}/>
        </div>
    )
};

export default BookList;
